sql-queries.sql-query:
  SELECT dt.district_name,dmim.head_id, dmstm.head_name, dmim.no_cases_rep_cm, dmim.no_cases_rep_lm, dmim.no_cases_rep_cmpy
FROM dist_mcr_ipc_month dmim
INNER JOIN dist_mcr_sub_tbl_month dmstm ON dmim.head_id=dmstm.head_id AND dmstm.head_rep_no='2A'
INNER JOIN district_table dt ON dt.id = dmim.district_id
WHERE dmim.report_month=${current_month}
AND dmim.report_year=${current_year}
AND dmim.rep_flag=0
GROUP BY dmim.district_id, dmim.head_id
ORDER BY dmim.district_id, dmim.head_id;

SELECT ri.id ,ri.report_name,ru.uploaded_date,ru.path_report,ru.district_id FROM reports_info ri  INNER JOIN
	report_uploaded_status ru ON ri.id = ru.report_id WHERE
ru.report_month=? AND ru.report_year=? AND ru.status=? AND ru.district_id=? AND report_week is null AND report_id !=22 ORDER BY ri.id;
